import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [recipes, setRecipes] = useState([]);


  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const { data } = await axios.get('http://localhost:3001/api/recipes/categories');
        setCategories(data.slice(0, 5)); // Directly slice the array since the response is an array
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };

    fetchCategories();
  }, []);



  // Fetch recipes when a category is selected
  useEffect(() => {
    if (selectedCategory) {
      const fetchRecipes = async () => {
        try {
          const { data } = await axios.get(`http://localhost:3001/api/recipes/category/${selectedCategory}`);
          setRecipes(data); // Assuming data contains an array of recipes
        } catch (error) {
          console.error("Failed to fetch recipes:", error);
        }
      };

      fetchRecipes();
    }
  }, [selectedCategory]);


    // Handle category click
    const handleCategoryClick = (category) => {
      setSelectedCategory(category);
    };

  return (
    // <div>
    //   <h2>Recipe Categories</h2>
    //   <ul>
    //     {categories.map((category) => (
    //       <li key={category.idCategory}>
    //         {category.strCategory}
    //       </li>
    //     ))}
    //   </ul>
    // </div>
<>
    <div className="dashboardcontainer">
      <h2>Recipe Categories</h2>
      {/* Category Buttons */}
      <div className="categorybuttons">
        {categories.map((category) => (
          <button
            key={category.idCategory}
            className={selectedCategory === category.strCategory ? 'selected' : ''}
            onClick={() => handleCategoryClick(category.strCategory)}
          >
            {category.strCategory}
          </button>
        ))}
      </div>
      </div>

        {/* Recipe Grid */}
      <div className="recipeGrid">
        {recipes.map((recipe) => (
          <div key={recipe.idMeal} className="recipe-card">
            <img
              src={recipe.strMealThumb}
              alt={recipe.strMeal}
              className="recipe-image"
            />
            <h3 className="recipe-title">{recipe.strMeal}</h3>
            <span className="recipe-favorite">â™¡</span>
          </div>
        ))}
      </div>

      </>
  );
};

export default Dashboard;
