import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/favorite.css';


const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      const token = localStorage.getItem('token');
      const { data } = await axios.get('http://localhost:3001/api/recipes/favorites', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFavorites(data);
    };

    fetchFavorites();
  }, []);

  // const handleRemove = async (idMeal) => {
  //   const token = localStorage.getItem('token');
  //   await axios.delete(`http://localhost:3001/api/recipes/favorites/${idMeal}`, {
  //     headers: { Authorization: `Bearer ${token}` }
  //   });
  //   setFavorites(favorites.filter(fav => fav._id !== idMeal));
  // };

  const handleRemove = async (idMeal) => {
    const token = localStorage.getItem('token');

    try {
      await axios.delete(`http://localhost:3001/api/recipes/favorites/${idMeal}`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      // Update the state by removing the recipe by idMeal, not _id
      setFavorites(favorites.filter(fav => fav.idMeal !== idMeal));

      console.log(`Recipe with idMeal ${idMeal} removed`);
    } catch (error) {
      console.error('Failed to remove favorite:', error);
    }
  };

  return (
    <div className="favGrid">



        {favorites.map((recipe) => (
         <div key={recipe._id} className="fav-card" >


            <img
              src={recipe.strMealThumb}
              alt={recipe.strMeal}
              className="fav-image"

            />
             {recipe.strMeal}
            <div className=' mt-2'>

            <button className='remove-button' onClick={() => handleRemove(fav.idMeal)}>Remove</button>
            </div>
    </div>

        ))}

      </div>


  );
};

export default Favorites;
