import React from 'react';
import Modal from 'react-modal';
import '../styles/recipedetailsmodal.css';

// Ensure that the app element is set for accessibility purposes
Modal.setAppElement('#root');

const RecipeDetailsModal = ({ isOpen, onRequestClose, recipe }) => {
  console.log("RecipeDetailsModal is rendering, isOpen:", isOpen, "Recipe:", recipe);

  return (
    <Modal isOpen={isOpen} onRequestClose={onRequestClose} className="modal" overlayClassName="modal-overlay">
      <h2>{recipe?.strMeal}</h2>
      <p>{recipe?.strInstructions || 'Instructions not available.'}</p>
      <img src={recipe?.strMealThumb} alt={recipe?.strMeal} className="modal-image" />
      <button onClick={onRequestClose} className="close-modal-button">Close</button>
    </Modal>
  );
};

export default RecipeDetailsModal;
