      if (strCategory) {
        const fetchRecipes = async () => {
          try {
            const { data } = await axios.get(`http://localhost:3001/api/recipes?category=${strCategory}`);
            setRecipes(data); // Assume `data` contains an array of recipes
          } catch (error) {
            console.error("Failed to fetch recipes:", error);
          }
        };

        fetchRecipes();
      }
    }, [strCategory]);

    // Handle category click
    const handleCategoryClick = (category) => {
      setSelectedCategory(category);
    };

  return (
