    //       <li key={category.idCategory}>
    //         {category.strCategory}
    //       </li>
    //     ))}
    //   </ul>
    // </div>
<>
    <div className="dashboardcontainer">
      <h2>Recipe Categories</h2>
      {/* Category Buttons */}
      <div className="categorybuttons">
        {categories.map((category) => (
          <button
            key={category.idCategory}
            className={selectedCategory === category.strCategory ? 'selected' : ''}
            onClick={() => handleCategoryClick(category.strCategory)}
          >
            {category.strCategory}
          </button>
        ))}
      </div>
      </div>
       {/* Recipe Grid */}
       <div className="recipe-grid">
        {recipes.map((recipe, index) => (
          <div key={index} className="recipe-card">
            <div className="recipe-image-placeholder"></div>
            <p className="recipe-category">Soups</p> {/* Example: Hardcoded as Soups */}
            <h3 className="recipe-title">Chicken Noodle Soup</h3> {/* Example: Hardcoded */}
            <span className="recipe-favorite">â™¡</span>
          </div>
        ))}
      </div>
      </>
  );
};

