import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/dashboard.css';
import RecipeDetailsModal from './RecipeDetails'; // Modal component


const Dashboard = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null); // For storing the selected recipe for the modal
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const { data } = await axios.get('http://localhost:3001/api/recipes/categories');
        setCategories(data.slice(0, 5)); // Directly slice the array since the response is an array
      } catch (error) {
        console.error("Failed to fetch categories:", error);
      }
    };
    fetchCategories();
  }, []);

  // Fetch recipes when a category is selected
  useEffect(() => {
    if (selectedCategory) {
      const fetchRecipes = async () => {
        try {
          const { data } = await axios.get(`http://localhost:3001/api/recipes/category/${selectedCategory}`);
          setRecipes(data); // Assuming data contains an array of recipes
        } catch (error) {
          console.error("Failed to fetch recipes:", error);
        }
      };

      fetchRecipes();
    }
  }, [selectedCategory]);

  // Handle category click
  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  // Handle recipe click (opens modal)
  const handleRecipeClick = (recipe) => {
    console.log("Recipe clicked:", recipe);  // Check if this logs
    setSelectedRecipe(recipe);
    setIsModalOpen(true); // Open the modal when a recipe is clicked
    console.log("Modal open state:", isModalOpen);  // Check if modal state is updating
  };

  // Close the modal
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedRecipe(null); // Clear the selected recipe when modal closes
  };


  useEffect(() => {
    const fetchFavorites = async () => {
      const token = localStorage.getItem('token');
      const { data } = await axios.get('http://localhost:3001/api/recipes/favorites', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFavorites(data);
    };

    fetchFavorites();
  }, []);

  const handleFavoriteClick = async (recipe) => {
    const token = localStorage.getItem('token');

    try {
      if (favorites.includes(recipe.idMeal)) {
        await axios.delete(`/api/recipes/favorites/${recipe.idMeal}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFavorites(favorites.filter(id => id !== recipe.idMeal));
      } else {
        await axios.post('/api/recipes/favorites', { recipeId: recipe.idMeal }, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setFavorites([...favorites, recipe.idMeal]);
      }
    } catch (error) {
      console.error('Failed to update favorite recipes:', error);
    }
  };

  return (
    <>
      <div className="dashboardcontainer">
        <h2>Recipe Categories</h2>
        {/* Category Buttons */}
        <div className="categorybuttons">
          {categories.map((category) => (
            <button
              key={category.idCategory}
              className={selectedCategory === category.strCategory ? 'selected' : ''}
              onClick={() => handleCategoryClick(category.strCategory)}
            >
              {category.strCategory}
            </button>
          ))}
        </div>
      </div>


      <div className="recipeGrid">
        {recipes.map((recipe) => (
          <div key={recipe.idMeal} className="recipe-card" >
            <img
              src={recipe.strMealThumb}
              alt={recipe.strMeal}
              className="recipe-image"
              onClick={() => handleRecipeClick(recipe)}
            />
           <div className='mt-2'>
              <span
                className={`recipe-favorite ${favorites.includes(recipe.idMeal) ? 'favorite-active' : ''}`}
                onClick={() => handleFavoriteClick(recipe)}
              >
                {favorites.includes(recipe.idMeal) ? '♥' : '♡'}
              </span>
            </div>
            <h3 className="recipe-title">{recipe.strMeal}</h3>
          </div>
        ))}
      </div>


      {selectedRecipe && (
        <RecipeDetailsModal
          isOpen={isModalOpen}
          onRequestClose={closeModal}
          recipe={selectedRecipe}
        />
      )}
    </>
  );
};

export default Dashboard;
