// MyAlertDialog.js
import React, { useState } from 'react';
import { AlertDialog, AlertDialogTrigger, AlertDialogContent, AlertDialogHeader, AlertDialogFooter, AlertDialogCancel, AlertDialogAction } from 'shadcn-ui';
import '../../'; // Import your custom CSS

const MyAlertDialog = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleDelete = () => {
    console.log("Item deleted");
    setIsOpen(false); // Close the dialog after the action
  };

  return (
    <div className="dialog-overlay">
      <AlertDialog open={isOpen} onOpenChange={setIsOpen}>
        <AlertDialogTrigger asChild>
          <button className="dialog-button action">Delete Item</button>
        </AlertDialogTrigger>
        <AlertDialogContent className="dialog-content">
          <AlertDialogHeader className="dialog-header">
            <h2>Are you sure?</h2>
          </AlertDialogHeader>
          <p>This action cannot be undone. This will permanently delete your item.</p>
          <AlertDialogFooter className="dialog-footer">
            <AlertDialogCancel className="dialog-button cancel">Cancel</AlertDialogCancel>
            <AlertDialogAction className="dialog-button action" onClick={handleDelete}>
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
};

export default MyAlertDialog;
