import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/favorite.css';


const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const fetchFavorites = async () => {
      const token = localStorage.getItem('token');
      const { data } = await axios.get('http://localhost:3001/api/recipes/favorites', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setFavorites(data);
    };

    fetchFavorites();
  }, []);

  const handleRemove = async (recipeId) => {
    const token = localStorage.getItem('token');
    await axios.delete(`http://localhost:3001/api/recipes/favorites/${recipeId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setFavorites(favorites.filter(fav => fav._id !== recipeId));
  };

  return (
    <div className="favGrid">
      <h2>Your Favorite Recipes</h2>
      <div></div>
      <ul>
        {favorites.map((recipe) => (
          <li key={recipe._id} >

            {recipe.strMeal}
            <img
              src={recipe.strMealThumb}
              alt={recipe.strMeal}
              className="fav-image"

            />
            <div className=' mt-2'>
            <button className='remove-button' onClick={() => handleRemove(recipe._id)}>Remove</button>
            </div>

          </li>
        ))}
      </ul>
    </div>
  );
};

export default Favorites;
