import React from 'react'
import { Link } from 'react-router-dom'
import '../styles/navbar.css'
import { useNavigate } from 'react-router-dom';

const NavBar = () => {

    const navigate = useNavigate();

    const handleLogout = () => {
      // Clear authentication token or session info
      localStorage.removeItem('token'); // Assuming you stored the JWT in localStorage

      // Optionally, you can clear more user data if needed
      // localStorage.removeItem('userData');

      // Redirect user to the login page
      window.href('/login');
      window.location.reload('');
    };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <span className="logo-text">co<span className="logo-k">o</span>k</span>
      </div>
      <div className="navbar-links">
        <Link to="/homepage" className="nav-link">HOME</Link>
        <Link to="/favorite" className="nav-link">FAVOURITE</Link>
      </div>
      <div className="navbar-logout">
        <button onClick={handleLogout} className="logout-button">Logout</button>
        <i className="logout-icon">âž¡</i> {/* Can replace with an SVG or FontAwesome icon */}
      </div>
    </nav>
  )
}

export default NavBar
