import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import HomePage from './pages/HomePage';
import Favorites from './components/Favorites';
import NavBar from './components/NavBar'; // Assuming NavBar is in components

function App() {
  const isAuthenticated = !!localStorage.getItem('authToken');  // Check if token exists

  return (
    <Router>
      {isAuthenticated && <NavBar />}  {/* Conditionally render NavBar only if logged in */}
      <Routes>
        {/* Public Routes */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Protected Routes */}
        <Route path="/das" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" />} />
        <Route path="/favorites" element={isAuthenticated ? <Favorites /> : <Navigate to="/login" />} />

        {/* Default Route */}
        <Route path="/" element={isAuthenticated ? <HomePage /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
